import React, {Component} from 'react';
import axios from "axios";
import { useNavigate } from "react-router-dom";


function adduser(Component) {
    return props => <Component {...props} navigate={useNavigate()} />
}

class Home extends Component {

    state = {
        user: {},
    };

    onChangeInput = e => {
       this.setState({ user: { ...this.state.user, [e.target.name]: e.target.value } });
    };

    onSubmit = async e => {
        e.preventDefault();
            await axios.post("http://localhost:3000/cuser", this.state.user);
        this.props.navigate("/");
    };


    render() {

        const { name ="", email="", phone=""} = this.state.user

        return (
            <div className="container">
                <form onSubmit={e => this.onSubmit(e)}>
                    <div className="form-group mb-2">
                        <input
                            type="text"
                            className="form-control"
                            placeholder="enter name"
                            name="name"
                            onChange={e => this.onChangeInput(e)}
                        />
                    </div>

                    <div className="form-group mb-2">
                        <input
                            type="text"
                            className="form-control"
                            placeholder="enter email"
                            name="email"
                        />
                    </div>

                    <div className="form-group mb-2">
                        <input
                            type="text"
                            className="form-control"
                            placeholder="enter phone"
                            name="phone"
                        />
                    </div>

                    <div className="form-group mb-2 d-flex">
                        <label><strong>Gender:</strong></label>
                        <div className="form-check">
                            <input className="form-check-input" type="radio" name="gender"/>
                            <label className="form-check-label">male</label>
                        </div>
                        <div className="form-check">
                            <input className="form-check-input" type="radio" name="gender"/>
                            <label className="form-check-label">female</label>
                        </div>
                    </div>

                    <div className="form-group mb-2 d-flex">
                        <label><strong>Vehicle:</strong></label>
                        <div className="form-check">
                            <input className="form-check-input" type="checkbox" name="gender"/>
                            <label className="form-check-label">car</label>
                        </div>
                        <div className="form-check">
                            <input className="form-check-input" type="checkbox" name="gender"/>
                            <label className="form-check-label">bike</label>
                        </div>
                    </div>

                    <div className="form-group my-2 d-flex">
                        <label><strong>Country:</strong></label>
                        <select name="country">
                            <option value={""} selected="selected"></option>
                            <option value={"pakistan"}>pakistan</option>
                            <option value={"africa"}>africa</option>
                            <option value={"china"}>china</option>
                            <option value={"other"}>other</option>
                        </select>
                    </div>

                    <button className="btn btn-primary btn-block">Add User</button>

                </form>
            </div>
        );
    }
}

export default adduser(Home);
